drop table if exists definition cascade;
drop table if exists dictionary_entry cascade;
drop table if exists dictionary_entry_parts_of_speech cascade;
drop table if exists quote cascade;
drop table if exists verb_morphology_entry cascade;
drop table if exists verb_morphology_entry_parts_of_speech cascade;
create table definition (dictionary_entry_id bigint, id bigint generated by default as identity, mark varchar(255), source varchar(255), definition TEXT, primary key (id));
create table dictionary_entry (id bigint generated by default as identity, entry_word varchar(255), head_word varchar(255), source varchar(255), synonym varchar(5000) array, primary key (id));
create table dictionary_entry_parts_of_speech (dictionary_entry_id bigint not null, parts_of_speech varchar(255));
create table quote (dictionary_entry_id bigint, id bigint generated by default as identity, author varchar(1000), source varchar(255), text TEXT, primary key (id));
create table verb_morphology_entry (dictionary_entry_id bigint, id bigint generated by default as identity, conjugated_form varchar(255), primary key (id));
create table verb_morphology_entry_parts_of_speech (verb_morphology_entry_id bigint not null, parts_of_speech varchar(255));
alter table if exists definition add constraint FK9mjuy65y5608pxcrssy8yvv4g foreign key (dictionary_entry_id) references dictionary_entry;
alter table if exists dictionary_entry_parts_of_speech add constraint FK8uxjg43kh6hvk5lidbg4wyyvl foreign key (dictionary_entry_id) references dictionary_entry;
alter table if exists quote add constraint FKcvx5u3a5e9ot9gvw0wg085h1l foreign key (dictionary_entry_id) references dictionary_entry;
alter table if exists verb_morphology_entry add constraint FKja4i6a2nttihadupbkw96almo foreign key (dictionary_entry_id) references dictionary_entry;
